        </template>

        <v-list>
          <v-list-item @click="trangCaNhan">
            <v-list-item-title>
              <v-icon>mdi-account</v-icon>Trang cÃ¡ nhÃ¢n
            </v-list-item-title>
          </v-list-item>
          <v-list-item @click="logout">
            <v-list-item-title>
              <v-icon>mdi-backup-restore</v-icon>ÄÄng xuáº¥t
            </v-list-item-title>
          </v-list-item>
        </v-list>
      </v-menu>
      <v-btn v-if="loggedIn" class="ma-2" tile outlined color="white" height="30px">{{name}}</v-btn>
